// SINGLE PRODUCT DETAILS PAGE TEMPLATE
// Dynamically generated by gatsby
import React from "react"
import { graphql, Link } from "gatsby"
import Image from "gatsby-image"
import Layout from "../components/ui/Layouts/layout"
import ReactMarkdown from "react-markdown"

// Destructuring all the properties from the query
const SingleProduct = ({
  data: {
    product: {
      title,
      price,
      image: {
        childImageSharp: { fixed },
      },
      description,
    },
  },
}) => {
  return (
    <Layout>
      <section className="SingleProduct container--">
        <aside>
          <Image fixed={fixed} alt={title} />
        </aside>
        <article>
          <h1 className="h3">{title}</h1>
          <h3>
            Replacement unit installed from{" "}
            <span style={{ color: `primary` }}>${price}</span> (based on back to
            back 3 metre pipe length)
          </h3>
          <ReactMarkdown source={description} />
          <Link to="/contact" className="addToCartBtn">
            Contact me
          </Link>
          <Link
            style={{ textAlign: "center", marginLeft: "20px" }}
            to="/products"
          >
            back to products
          </Link>
        </article>
      </section>
    </Layout>
  )
}

export const query = graphql`
  query GetProducts($url: String) {
    product: strapiProduct(url: { eq: $url }) {
      title
      price
      description
      id
      image {
        childImageSharp {
          fixed(width: 325) {
            base64
            width
            height
            src
            srcSet
          }
        }
      }
    }
  }
`

export default SingleProduct
